CREATE TABLE `APP_WASTE_LOCATION_REPORTING` (
  `WASTE_LOCATION_ID` varchar(25) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL,
  `WASTE_REPORTING_ID` varchar(25) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL,
  PRIMARY KEY (`WASTE_LOCATION_ID`,`WASTE_REPORTING_ID`) USING BTREE
) ENGINE=InnoDB DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC;


CREATE TABLE `APP_WASTE_LOCATION_TYPES` (
  `WASTE_LOCATION_ID` varchar(25) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL DEFAULT '',
  `WASTE_TYPE_ID` varchar(25) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL DEFAULT '',
  PRIMARY KEY (`WASTE_LOCATION_ID`,`WASTE_TYPE_ID`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

CREATE TABLE `APP_WASTE_REASONS` (
  `WASTE_REASON_ID` varchar(25) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL DEFAULT '',
  `DESCRIPTION` varchar(80) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci DEFAULT '',
  `ENABLED` varchar(1) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci DEFAULT 'Y',
  PRIMARY KEY (`WASTE_REASON_ID`) USING BTREE
) ENGINE=InnoDB DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC;

CREATE TABLE `APP_WASTE_REPORTING_GROUP` (
  `REPORTING_GROUP` int NOT NULL DEFAULT '-1',
  `DESCRIPTION` varchar(80) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci DEFAULT '',
  `ENABLED` varchar(1) DEFAULT 'Y',
  PRIMARY KEY (`REPORTING_GROUP`)
