DDL.statement:
CREATE FUNCTION `c4j_GetYear`(XDATE datetime,XSIZE int) RETURNS int(11) NOT DETERMINISTIC READS SQL DATA
BEGIN
	DECLARE result varchar(4) CHARACTER SET utf8;

	SET result = RIGHT(CAST(YEAR(XDATE) AS CHAR(4)) ,XSIZE);
RETURN result;
END

CREATE FUNCTION `c4j_GetControlValue`(XNAME varchar(50)) RETURNS varchar(50) CHARSET utf8 NOT DETERMINISTIC READS SQL DATA
BEGIN
	DECLARE result varchar(50) CHARACTER SET utf8;
	SET result = (SELECT key_value FROM SYS_CONTROL where system_key = XNAME); 

RETURN result;
END

CREATE FUNCTION `c4j_GetPlant`() RETURNS varchar(10) CHARSET utf8 NOT DETERMINISTIC READS SQL DATA
BEGIN
	DECLARE result varchar(10) CHARACTER SET utf8;

	SET result = c4j_GetControlValue('PLANT');


RETURN result;
END

CREATE FUNCTION `c4j_GetMonth`(XDATE datetime) RETURNS varchar(2) CHARSET utf8 NOT DETERMINISTIC READS SQL DATA
BEGIN
	DECLARE result varchar(6) CHARACTER SET utf8;
	DECLARE moy int;

	SET moy = MONTH(XDATE);
	SET result = RIGHT(CONCAT('0',convert(moy,CHAR)),2);
RETURN result;
END

CREATE FUNCTION `c4j_GetBaseQuantity`(XMATERIAL varchar(20),XPRODUCTION_QUANTITY decimal(22,3),XPRODUCTION_UOM varchar(3)) RETURNS decimal(22,3) NOT DETERMINISTIC READS SQL DATA
BEGIN

DECLARE XBASE_UOM 	     varchar(3);
DECLARE XBASE_QUANTITY	  decimal(22,3);
		
		SET XBASE_UOM = (SELECT BASE_UOM FROM APP_MATERIAL WHERE MATERIAL = XMATERIAL);
	 	SET XBASE_QUANTITY = (SELECT (XPRODUCTION_QUANTITY * NUMERATOR / DENOMINATOR) FROM APP_MATERIAL_UOM WHERE MATERIAL = XMATERIAL AND UOM = XPRODUCTION_UOM);

RETURN XBASE_QUANTITY;

END

CREATE FUNCTION `c4j_GetDay`(XDATE datetime) RETURNS varchar(2) CHARSET utf8 NOT DETERMINISTIC READS SQL DATA
BEGIN

	DECLARE result varchar(2) CHARACTER SET utf8;
	DECLARE dom int;

	SET dom = DAYOFMONTH(XDATE);
	SET result = RIGHT(CONCAT('0',convert(dom,CHAR)),2);

RETURN result;
END

CREATE FUNCTION `c4j_GetExpiryDate`(XPRODUCTION_DATE datetime,XSHELF_LIFE int,XSHELF_LIFE_UOM varchar(1),XSHELF_LIFE_ROUNDING varchar(1)) RETURNS datetime NOT DETERMINISTIC READS SQL DATA
BEGIN
	DECLARE result datetime;

	SET result = XPRODUCTION_DATE;


		IF XSHELF_LIFE_UOM = 'D' THEN
			SET result =  DATE_ADD(result,INTERVAL XSHELF_LIFE DAY);
		END IF;

		IF XSHELF_LIFE_UOM = 'M' THEN
			SET result =  DATE_ADD(result,INTERVAL XSHELF_LIFE MONTH);
		END IF;

		IF XSHELF_LIFE_UOM = 'Y' THEN
			SET result =  DATE_ADD(result,INTERVAL XSHELF_LIFE YEAR);
		END IF;
			
		IF XSHELF_LIFE_ROUNDING = '+' THEN
			SET result = LAST_DAY(result);
		END IF;

		IF XSHELF_LIFE_ROUNDING = '-' THEN
			SET result = ADDDATE(LAST_DAY(SUBDATE(result, INTERVAL 1 MONTH)), 1);
		END IF;

		SET result = DATE(result);

RETURN result;
END

CREATE FUNCTION `c4j_GetFinalBatchNumber`(XPRODUCTION_DATE datetime,XOVERRIDE_BATCH varchar(1),XOVERRIDE_BATCH_NUMBER varchar(40),XBATCH_OPTIONAL varchar(40)) RETURNS varchar(100) CHARSET utf8 NOT DETERMINISTIC READS SQL DATA
BEGIN
	DECLARE result varchar(100) CHARACTER SET utf8;
	DECLARE batchformat varchar(100) CHARACTER SET utf8;

	IF XOVERRIDE_BATCH = 'Y' THEN
		SET result = XOVERRIDE_BATCH_NUMBER;
	END IF;

	IF XOVERRIDE_BATCH = 'N' THEN
			SET batchformat = c4j_GetControlValue('BATCH FORMAT');
			SET result = batchformat;
			SET result = REPLACE(result,'{PLANT}',c4j_GetPlant());
			SET result = REPLACE(result,'{YEAR4}',c4j_GetYear(XPRODUCTION_DATE,4));
			SET result = REPLACE(result,'{YEAR2}',c4j_GetYear(XPRODUCTION_DATE,2));
			SET result = REPLACE(result,'{YEAR1}',c4j_GetYear(XPRODUCTION_DATE,1));
			SET result = REPLACE(result,'{MONTH}',c4j_GetMonth(XPRODUCTION_DATE));
			SET result = REPLACE(result,'{DAY}',c4j_GetDay(XPRODUCTION_DATE));
			SET result = REPLACE(result,'{JULIAN_DAY}',c4j_GetJulian(XPRODUCTION_DATE,1));
			SET result = REPLACE(result,'{JULIAN0_DAY}',c4j_GetJulian(XPRODUCTION_DATE,0));
			SET result = REPLACE(result,'{JULIAN1_DAY}',c4j_GetJulian(XPRODUCTION_DATE,1));
	END IF;

	SET result = CONCAT(result,XBATCH_OPTIONAL);

RETURN result;
END

CREATE FUNCTION `c4j_GetFinalExpiryDate`(XPRODUCTION_DATE datetime,XSHELF_LIFE int,XSHELF_LIFE_UOM varchar(1),XSHELF_LIFE_ROUNDING varchar(1),XOVERRIDE_EXPIRY varchar(1),XOVERRIDE_EXPIRY_DATE datetime) RETURNS datetime  NOT DETERMINISTIC READS SQL DATA
BEGIN
	DECLARE result datetime;


	IF XOVERRIDE_EXPIRY = 'Y' THEN
		SET result = XOVERRIDE_EXPIRY_DATE;
	END IF;


	IF XOVERRIDE_EXPIRY = 'N' THEN
		SET result = c4j_GetExpiryDate(XPRODUCTION_DATE,XSHELF_LIFE,XSHELF_LIFE_UOM,XSHELF_LIFE_ROUNDING);
	END IF;

RETURN result;
END

CREATE FUNCTION `c4j_GetFullPalletQuantity`(XPROCESS_ORDER varchar(40)) RETURNS decimal(22,3)   NOT DETERMINISTIC READS SQL DATA
BEGIN
	DECLARE result decimal(22,3);

	DECLARE xmaterial varchar(40) CHARACTER SET utf8;
	DECLARE xpal_uom varchar(3) CHARACTER SET utf8;
	DECLARE xpal_numerator decimal(22,3);
	DECLARE xpal_denominator decimal(22,3);
	DECLARE xrequired_uom varchar(3) CHARACTER SET utf8;
	DECLARE xrequired_denominator decimal(22,3);
	DECLARE xrequired_numerator decimal(22,3);

	SET xpal_uom = c4j_GetControlValue('UOM FULL PALLET QTY');
	

	SET xrequired_uom = (SELECT REQUIRED_UOM FROM APP_PROCESS_ORDER WHERE PROCESS_ORDER = XPROCESS_ORDER);
	SET xmaterial = (SELECT MATERIAL FROM APP_PROCESS_ORDER WHERE PROCESS_ORDER = XPROCESS_ORDER);
	SET xrequired_denominator = (SELECT DENOMINATOR FROM APP_MATERIAL_UOM WHERE MATERIAL = xmaterial and UOM = xrequired_uom);
	SET xrequired_numerator = (SELECT NUMERATOR FROM APP_MATERIAL_UOM WHERE MATERIAL = xmaterial and UOM = xrequired_uom);	

	SET xpal_denominator = (SELECT DENOMINATOR FROM APP_MATERIAL_UOM WHERE MATERIAL = xmaterial and UOM = xpal_uom);
	SET xpal_numerator = (SELECT NUMERATOR FROM APP_MATERIAL_UOM WHERE MATERIAL = xmaterial and UOM = xpal_uom);

	SET result = (xpal_numerator/xpal_denominator) * (xrequired_denominator/xrequired_numerator);

    RETURN result;
END

CREATE FUNCTION `c4j_GetJulian`(XDATE datetime,XOFFSET int) RETURNS varchar(6) CHARSET utf8   NOT DETERMINISTIC READS SQL DATA
BEGIN
	DECLARE result VARCHAR(6) CHARACTER SET utf8;
	DECLARE dayofyear int;

	SET dayofyear = DAYOFYEAR(XDATE)-1+XOFFSET;

	SET result = RIGHT(CONCAT('000',convert(dayofyear,CHAR)),3);

RETURN result;
END

CREATE FUNCTION `c4j_GetLayers`(XPROCESS_ORDER varchar(20)) RETURNS int(11)   NOT DETERMINISTIC READS SQL DATA
BEGIN
	DECLARE result 					int;
	DECLARE xlayers_uom 				varchar(3) CHARACTER SET utf8;
	DECLARE xpallet_uom 				varchar(3) CHARACTER SET utf8;
	DECLARE xbase_quantity 			decimal(22,3);
	DECLARE xmaterial					varchar(20) CHARACTER SET utf8;
	DECLARE xproduction_uom  		varchar(3) CHARACTER SET utf8;
	DECLARE xproduction_quantity 	decimal(22,3);

	SET xproduction_quantity = c4j_GetFullPalletQuantity(XPROCESS_ORDER);
	SET xmaterial = (SELECT MATERIAL FROM APP_PROCESS_ORDER WHERE PROCESS_ORDER = XPROCESS_ORDER);
	SET xproduction_uom = (SELECT REQUIRED_UOM FROM APP_PROCESS_ORDER WHERE PROCESS_ORDER = XPROCESS_ORDER);
	SET xbase_quantity = c4j_GetBaseQuantity(XMATERIAL,XPRODUCTION_QUANTITY,XPRODUCTION_UOM);

	SET xlayers_uom = c4j_GetControlValue('UOM BASE QTY PER LAYER');

	SET result = (SELECT (xbase_quantity / NUMERATOR * DENOMINATOR) FROM APP_MATERIAL_UOM WHERE MATERIAL = xmaterial AND UOM = xlayers_uom);
	
	SET result = CEILING(result);
	SET result = COALESCE(result,1);

	IF result = 0 THEN 
		SET result = 1;
	END IF;

    RETURN result;
END
